import { useEffect } from "react";
import { useSelector } from "react-redux";
import { useDispatch } from "react-redux";
import { getUsers } from "../../actions/index";
import Student from "./card/card";
import { Button } from "bootstrap";
import Form from 'react-bootstrap/Form';
import { Grid, CircularProgress } from '@material-ui/core';
import { Link } from "react-router-dom";
// import {CircularProgress} from '@mui/material/CircularProgress';

function Card(){
    
    const users=useSelector((state)=>state.user.data)
    const dispatch=useDispatch()
   useEffect(()=>{
     dispatch(getUsers())
    
   },[dispatch])
  
    return(
        <>
       
        <Form.Select aria-label="Default select example"  >
      <option> <h1> Choose Peoples with your interest</h1></option>
      <option value="male">Male</option>
      <option value="female">Female</option>
    </Form.Select>
        <Link to="/home">
        <button style={{marginTop:'2rem'}}>back to home</button>
        </Link>
      
          {
          users ?(
            <Grid  container alignItems="stretch" spacing={1} style={{marginLeft:'2rem'}}>
        {users.map((user) => (
          <Grid key={user.id} item xs={12} sm={6} md={3}>
          <Student user={user}/>
          </Grid>
        ))}
      </Grid>
         ):<CircularProgress/>
          }
        </>
    )
}

export default Card
