import { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import { useDispatch } from "react-redux";
import { getUsers,getbydomain } from "../../actions/index";
import Student from "./card/card";
import { Button } from "bootstrap";
import Form from 'react-bootstrap/Form';
import { Grid, CircularProgress } from '@material-ui/core';
import { Link } from "react-router-dom";
function Card(){
    const [stream,setStream]=useState("ALL")
    const user1=useSelector((state)=>state.users.data)
    const [user,setUser]=useState(user1)

  
    useEffect(()=>{
      if(stream==="ALL")setUser(user1);
      else{
       const dom=user.filter(data=>{
          return stream===data.domain 
  })
  console.log();
  setUser(dom)
      }
    },[stream])

  
    useEffect(() => {
     
      localStorage.setItem("user",user);
    });

    // console.log((Object.keys(users).length >0));
  //  domain==="ALL"?dispatch(getUsers()):dispatch(getbydomain())
    return(
        <>
        <Form.Select aria-label="Default select example"
        onChange={(e)=>  setStream(e.target.value)}  >
    <h1> Choose Peoples with your interest</h1>
      <option key="ALL"  value="ALL">ALL</option>
      <option key="UPSC"  value="UPSC">UPSC</option>
      <option key="IIT"  value="IIT">IIT</option>
      <option key="NEET"  value="NEET">NEET</option>
      <option key="UPPCS"  value="UPPCS">UPPCS</option>
      <option key="NDA"  value="NDA">NDA</option>
      <option key="CDS"  value="CDS">CDS</option>
    </Form.Select>
        <Link to="/home">
        <button style={{marginTop:'5rem'}}>back to home</button>
        </Link>
          {
            (Object.keys(user).length >0) ?(
            <Grid  container alignItems="stretch" spacing={1} style={{marginLeft:'2rem'}}>
        {user.map((user) => (
          <Grid key={user.id} item xs={12} sm={6} md={3}>
          <Student user={user}/>
          </Grid>
        ))}
      </Grid>
         ):"no such student"
          }
        </>
    )
}

export default Card
