import { FETCH_USERS, SEND,GET_BY_DOMAIN, START_LOADING, END_LOADING } from "../components/constants";

export default(state = { isLoading: true, users: [] }, action) =>{
   
    switch(action.type){
        case START_LOADING:
           return { ...state, isLoading: true };
        case END_LOADING:
            return {...state,isloading:false};
        case SEND:
            return {...state,users:[...state.posts,action]};
        case FETCH_USERS:
            return {...state,users:action.payload};
        case GET_BY_DOMAIN:
             return {...state,users:action.payload};
        default:
            return state;
    }

};
