import React from "react";
import { useSearchParams } from "react-router-dom";
import { getbyId } from "../../actions";
import { useEffect } from "react";
import { useDispatch } from "react-redux";
import { useParams } from "react-router-dom";
import { useSelector } from "react-redux";
import "./details.css"

function Details(props) {
  const id = useParams().id;
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getbyId(id));
  }, []);
  let { isloading, users } = useSelector((state) => state.users);
  console.log(users);
  return (
    <div className="details-main">

      <div className="photo">
        <img src={users.file} />
      </div>

      <div className="data">
        <h2>{users.username}</h2>
        <p>{users.email}</p>
        <p>
          {users.about
            ? users.about
            : `{hey nerds! i'm intense focused,
     contact me if you wanna make me your academic partner}`}
        </p>

        <div className="social-accounts">
        {users.facebook? <a href={users.facebook}>facebook</a>:"Doesn't have facebook account" }<br/>
        {users.instagram? <a href={users.instagram}>Instagram</a>:"Doesn't have instagram account" }
        </div>
      </div>
    </div>
  );
}

export default Details;
