import { getUsers,getbydomain } from "../../actions/index";
import Student from "./card/card";
import { Button } from "bootstrap";
import Form from 'react-bootstrap/Form';
import { Grid, CircularProgress } from '@material-ui/core';
import { Link } from "react-router-dom";
function Card(props){
 
  let user3=[{
    "username":"ujjawal tyagi",
    "email":"tyagiujjawal763@gmail.com",
    "password":"123",
    "domain":"IIT",
    "gender":"male",
    "about":"i've been preparing since 2 years but still i'm confused.."
      }]
    const [stream,setStream]=useState("ALL")
    const {isloading,user1}=useSelector((state)=>state.users)
    console.log(isloading);
    const [user,setUser]=useState(user3)
